const express = require('express');
const routes = require('./routes');
const middleware = require('./middleware/logger');

const app = express();
app.use(middleware);
app.use('/api', routes);

module.exports = app;

module.exports = {
  host: process.env.DB_HOST || 'localhost',
  port: process.env.PORT || 3000,
  database: 'webapp_db'
};

module.exports = {
  jwtSecret: process.env.JWT_SECRET || 'dev-secret',
  tokenExpiry: '24h'
};

const router = require('express').Router();
const users = require('./users');
const posts = require('./posts');

router.use('/users', users);
router.use('/posts', posts);

module.exports = router;

const router = require('express').Router();
const UserController = require('../controllers/UserController');

router.get('/', UserController.list);
router.post('/', UserController.create);
router.get('/:id', UserController.getById);

module.exports = router;

const router = require('express').Router();
const PostController = require('../controllers/PostController');

router.get('/', PostController.list);
router.post('/', PostController.create);
router.get('/:id', PostController.getById);

module.exports = router;

const UserService = require('../services/UserService');

class UserController {
  static async list(req, res) {
    const users = await UserService.findAll();
    res.json(users);
  }
  static async create(req, res) {
    const user = await UserService.create(req.body);
    res.status(201).json(user);
  }
  static async getById(req, res) {
    const user = await UserService.findById(req.params.id);
    res.json(user);
  }
}

module.exports = UserController;

const PostService = require('../services/PostService');

class PostController {
  static async list(req, res) {
    const posts = await PostService.findAll();
    res.json(posts);
  }
  static async create(req, res) {
    const post = await PostService.create(req.body);
    res.status(201).json(post);
  }
  static async getById(req, res) {
    const post = await PostService.findById(req.params.id);
    res.json(post);
  }
}

module.exports = PostController;

const User = require('../models/User');

class UserService {
  static async findAll() { return User.findAll(); }
  static async findById(id) { return User.findById(id); }
  static async create(data) { return User.create(data); }
}

module.exports = UserService;

const Post = require('../models/Post');

class PostService {
  static async findAll() { return Post.findAll(); }
  static async findById(id) { return Post.findById(id); }
  static async create(data) { return Post.create(data); }
}

module.exports = PostService;

