// Task A: This function needs improvement
// Try asking Claude with a VAGUE request like:
// "make this better" or "improve this code"

function process(d) {
  var r = [];
  for (var i = 0; i < d.length; i++) {
    if (d[i] > 0) {
      r.push(d[i] * 2);
    }
  }
  return r;
}

module.exports = { process };

// Task B: This function also needs improvement
// Try asking Claude with a SPECIFIC request like:
// "Rename this function to 'doublePositiveNumbers', use descriptive
// variable names, convert to arrow function with filter/map"

function proc(arr) {
  var out = [];
  for (var j = 0; j < arr.length; j++) {
    if (arr[j] > 0) {
      out.push(arr[j] * 2);
    }
  }
  return out;
}

module.exports = { proc };
